import React, { useState } from "react";
import "./App.css";

function App() {
  const [mode, setMode] = useState("signup"); // "signup" or "login"

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const data = Object.fromEntries(formData.entries());

    try {
      // Point to your local backend
      const endpoint =
        mode === "signup"
          ? "http://localhost:4000/api/signup"
          : "http://localhost:4000/api/login";

      const res = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });

      const json = await res.json();
      console.log("API response:", json);

      if (!json.ok) {
        alert(json.error || "Something went wrong");
        return;
      }

      // store JWT token for later use
      if (json.token) {
        localStorage.setItem("bytebite_token", json.token);
      }

      alert(
        mode === "signup"
          ? "Account created and saved to MongoDB!"
          : "Logged in successfully!"
      );

      // TODO: redirect to dashboard once you build it
      // window.location.href = "/dashboard";
    } catch (err) {
      console.error(err);
      alert("Network error talking to backend");
    }
  };

  const goAuth = (newMode) => {
    setMode(newMode);
    const el = document.getElementById("auth-card");
    if (el) {
      el.scrollIntoView({ behavior: "smooth", block: "center" });
    }
  };

  return (
    <div className="app">
      {/* Top nav */}
      <header className="nav">
        <div className="nav-left">
          <div className="logo">ByteBite</div>
        </div>
        <nav className="nav-links">
          <a href="#features">Features</a>
          <a href="#how-it-works">How it works</a>
        </nav>
        <div className="nav-right">
          <button
            className="nav-button nav-ghost"
            type="button"
            onClick={() => goAuth("login")}
          >
            Log in
          </button>
          <button
            className="nav-button nav-primary"
            type="button"
            onClick={() => goAuth("signup")}
          >
            Sign up
          </button>
        </div>
      </header>

      {/* Hero + auth panel */}
      <main className="hero">
        <div className="hero-copy">
          <h1>
            Retro vibes.
            <br />
            <span className="accent">Modern food tracking.</span>
          </h1>
          <p className="hero-subtitle">
            ByteBite gives you a clean, 80s-arcade inspired dashboard for
            tracking meals and habits without the bloat. Log in, sign up, and
            start collecting your bytes.
          </p>

          <ul className="hero-bullets">
            <li>‚ö° Fast, keyboard-friendly logging</li>
            <li>üéõ Retro-inspired, modern-sharp UI</li>
            <li>üîê Built to plug into your own backend later</li>
          </ul>
        </div>

        <div className="auth-card" id="auth-card">
          <div className="auth-tabs">
            <button
              type="button"
              className={`auth-tab ${
                mode === "signup" ? "auth-tab-active" : ""
              }`}
              onClick={() => setMode("signup")}
            >
              Sign up
            </button>
            <button
              type="button"
              className={`auth-tab ${
                mode === "login" ? "auth-tab-active" : ""
              }`}
              onClick={() => setMode("login")}
            >
              Log in
            </button>
          </div>

          {/* Auth form */}
          <form className="auth-form" onSubmit={handleSubmit}>
            {mode === "signup" && (
              <label className="field">
                <span className="field-label">Username</span>
                <input
                  type="text"
                  name="username"
                  autoComplete="username"
                  placeholder="byteguy42"
                  required
                />
              </label>
            )}

            <label className="field">
              <span className="field-label">Email</span>
              <input
                type="email"
                name="email"
                autoComplete="email"
                placeholder="you@example.com"
                required
              />
            </label>

            <label className="field">
              <span className="field-label">Password</span>
              <input
                type="password"
                name="password"
                autoComplete={
                  mode === "signup" ? "new-password" : "current-password"
                }
                placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                required
              />
            </label>

            <button type="submit" className="auth-submit">
              {mode === "signup" ? "Create account" : "Log in"}
            </button>

            <p className="auth-switch">
              {mode === "signup" ? (
                <>
                  Already have an account?{" "}
                  <span onClick={() => setMode("login")}>Log in</span>
                </>
              ) : (
                <>
                  Need an account?{" "}
                  <span onClick={() => setMode("signup")}>Sign up</span>
                </>
              )}
            </p>
          </form>

          {/* Decorative retro panel */}
          <div className="auth-footer">
            <div className="scanline" />
            <p className="auth-footer-text">
              Session status:{" "}
              <span className="chip chip-online">offline (demo)</span>
            </p>
          </div>
        </div>
      </main>

      {/* Extra sections just for content / scroll */}
      <section id="features" className="section">
        <h2>Built like an arcade cabinet</h2>
        <p className="section-subtitle">
          Old-school energy with new-school usability. ByteBite keeps the bold
          colors and sharp edges, but ditches the clunky UX.
        </p>
        <div className="feature-grid">
          <div className="feature-card">
            <h3>Pixel-clean UI</h3>
            <p>
              Crisp typography, high-contrast panels, and just enough neon to
              feel nostalgic without burning your eyes.
            </p>
          </div>
          <div className="feature-card">
            <h3>Drop-in ready</h3>
            <p>
              Wire these auth forms to any backend: Firebase, Supabase, your
              own API, or something custom.
            </p>
          </div>
          <div className="feature-card">
            <h3>Keyboard-first</h3>
            <p>
              Tab through fields, hit Enter, done. Designed to be fast when
              you&apos;re actually using it every day.
            </p>
          </div>
        </div>
      </section>

      <section id="how-it-works" className="section muted">
        <h2>How you might plug it in</h2>
        <div className="steps">
          <div className="step">
            <span className="step-number">1</span>
            <h3>Create a backend endpoint</h3>
            <p>
              Expose <code>/api/signup</code> and <code>/api/login</code> on
              your server to receive these form values.
            </p>
          </div>
          <div className="step">
            <span className="step-number">2</span>
            <h3>Store users in a database</h3>
            <p>
              Hash passwords, save email/username, and return a token or
              session ID.
            </p>
          </div>
          <div className="step">
            <span className="step-number">3</span>
            <h3>Redirect to your dashboard</h3>
            <p>After success, send the user to your main ByteBite UI.</p>
          </div>
        </div>
      </section>

      <footer className="footer">
        <p className="footer-meta">
          ¬© {new Date().getFullYear()} ByteBite. Built for devs who like pink
          and purple.
        </p>
      </footer>
    </div>
  );
}

export default App;
